        return new Source("{% extends 'base.html.twig' %}

{% block title %}Lista Usuarios{% endblock %}

{% block body %}
 <div style=\"text-align: center;\">
    <h1>Usuarios</h1>
    <br>
    <form action=\"{{ path('user_index')}}\" method=\"get\">
        <input type=\"submit\" value=\"Generar lista de Correos\" name=\"informe\">
    </form>
</div>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Id</th>
                <th>Email</th>
                <th>Roles</th>
                <th>Password</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for user in users %}
            <tr>
                <td>{{ user.id }}</td>
                <td>{{ user.email }}</td>
                <td>{{ user.roles ? user.roles|json_encode : '' }}</td>
                <td>{{ user.password }}</td>
                <td>
                    <a href=\"{{ path('user_show', {'id': user.id}) }}\">Ver</a>
                    <a href=\"{{ path('user_edit', {'id': user.id}) }}\">Editar</a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"5\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a href=\"{{ path('user_new') }}\">Crear nuevo Usuario</a>
{% endblock %}
