    <h1>Comentario index</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Id</th>
                <th>Text</th>
                <th>Autor</th>
                <th>Fecha</th>
                <th>Estado</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for comentario in comentarios %}
            <tr>
                <td>{{ comentario.id }}</td>
                <td>{{ comentario.text }}</td>
                <td>{{ comentario.autor }}</td>
                <td>{{ comentario.fecha ? comentario.fecha|date('Y-m-d H:i:s') : '' }}</td>
                <td>{{ comentario.estado ? 'Yes' : 'No' }}</td>
                <td>
                    <a href=\"{{ path('comentario_show', {'id': comentario.id}) }}\">show</a>
                    <a href=\"{{ path('comentario_edit', {'id': comentario.id}) }}\">edit</a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"6\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a href=\"{{ path('comentario_new') }}\">Create new</a>
{% endblock %}
